mapper.update:
UPDATE QNA_BOARD
SET COMMENT_COUNT = (
    SELECT COUNT(*)
    FROM COMM_QNA
    WHERE BOARD_ID = #{boardId}
)
WHERE BOARD_ID = #{boardId}

mapper.select:
select *
from (select rownum rnum, board_id, member_id,
title,
substance, read_count, regist_dt, last_upd_dt
from (select * from
qna_board
order by regist_dt desc, board_id desc))
where rnum between
#{
startRow } and #{ endRow }

mapper.delete:
delete from qna_board
where
board_ID = #{ no }

mapper.update:
UPDATE qna_board SET title = #{title}, member_ID = #{memberID}, substance = #{substance}, LAST_UPD_DT = sysdate, ORIGINAL_FILENAME =
null
#{originalFilePath}
, RENAME_FILENAME =
null
#{renameFilePath}
WHERE board_id = #{boardId}
