mapper.delete:
delete from COMM_QNA 
where comment_no = #{commentId} 
and BOARD_ID = #{boardId}

delete from COMM_QNA 
where comment_no = #{commentId} 
and BOARD_ID = #{boardId}

delete from COMM_QNA 
where REF_comment_no = #{commentId} 
and BOARD_ID = #{boardId}

mapper.update:
UPDATE COMM_QNA 
SET substance = #{substance}, 
LAST_UPD_DT = default 
where BOARD_ID = #{boardId}
and comment_no = #{commentId}
